package com.cg.bitwiseoperation;

public class bitwise {
	

		public static void main(String[] args) {
			// TODO Auto-generated method stub

			int a=8;
			int b=7;
			System.out.println("A & B = "+(a&b));
			System.out.println("A | B = "+(a^b));
			System.out.println("A ^ B = "+(a^b));
			System.out.println("~ A = "+(~ a));
			System.out.println("~ B = "+(~ b));
			System.out.println("A >> B = "+(a>>b));
			System.out.println("A << B = "+(a<<b));
			System.out.println("A >>> B = "+(a>>>b));
		
			
		}

	}

